SELECT DISTINCT temp_reconc.id AS id, temp_reconc.ohip AS Expr2, temp_reconc.service_code AS Expr3, temp_reconc.service_date AS Expr4, reconciliationclaimheader.ClaimNumber, reconciliationclaimitem.ExplanatoryCode, reconciliationclaimheader.HealthRegistrationNumber, reconciliationclaimitem.ServiceDate, reconciliationclaimitem.ServiceCode, reconciliationclaimitem.AmountSubmitted, reconciliationclaimitem.AmountPaid INTO temp_paid
FROM reconciliationclaimheader, reconciliationclaimitem, temp_reconc
WHERE (((reconciliationclaimheader.ClaimNumber)=[reconciliationclaimitem].[ClaimNumber]) AND (([temp_reconc].[ohip])=[reconciliationclaimheader].[HealthRegistrationNumber]) AND (([temp_reconc].[service_code])=[reconciliationclaimitem].[ServiceCode]) AND (([temp_reconc].[service_date])=CDate(Left([reconciliationclaimitem.ServiceDate],4) & ' / ' & Mid([reconciliationclaimitem.ServiceDate],5,2) & ' / ' & Right([reconciliationclaimitem.ServiceDate],2))) AND ((CDbl([reconciliationclaimitem].[AmountPaid]))>0));